// Trouver la date la plus rÃ©cente
let mostRecentDate = new Date(data[0]);
for (const dateStr of data) {
  const date = new Date(dateStr);
  if (date > mostRecentDate) {
    mostRecentDate = date;
  }
}

// GÃ©nÃ©rer les statistiques mensuelles
const statsMap = {};
for (const dateStr of data) {
  const date = new Date(dateStr);
  const year = date.getFullYear();
  const monthName = getMonthNameFr(dateStr);
  const key = `${year}-${monthName}`;
  statsMap[key] = (statsMap[key] || 0) + 1;
}

// Transformer en tableau triÃ© par annÃ©e/mois
const monthlyStats = Object.entries(statsMap)
  .map(([key, count]) => {
    const [year, monthName] = key.split('-');
    const date = new Date(`${year}-${(new Date(`${monthName} 1, ${year}`)).getMonth() + 1}-01`);
    return { year: Number(year), month: monthName, count, sortKey: date };
  })
  .sort((a, b) => a.sortKey - b.sortKey)
  .map(({ year, month, count }) => ({ year, month, count }));

// Retourner les rÃ©sultats
return [
  {
    json: {
      mostRecentDate: mostRecentDate.toISOString(),
      monthlyStats,
    },
  },
];

