={{$json.monthlyStats && Array.isArray($json.monthlyStats)
  ? $json.monthlyStats
      .slice(0, 10)
      .filter(item => item.month)
      .map(item => {
        const month = item.month.charAt(0).toUpperCase() + item.month.slice(1);
        const count = item.count;
        const label = `réaction${count > 1 ? 's' : ''}`;
        return `${month} ${item.year} = ${count} ${label}`;
      })
      .join('\n')
  : ''
}}
