// Validation et récupération des données d'entrée
const inputData = $input.first();
if (!inputData || !inputData.json) {
  throw new Error("Données d'entrée manquantes");
}
const contenu = inputData.json.Contenu || '';

// Validation et récupération du template HTML depuis le nœud "Data Workflow"
const dataWorkflowNode = $('Data Workflow').first();
if (!dataWorkflowNode || !dataWorkflowNode.json) {
  throw new Error("Données manquantes dans le nœud 'Data Workflow'");
}
const template = dataWorkflowNode.json["Template Content"] || '';

// Fonction utilitaire pour remplacer des placeholders dans une chaîne
function replacePlaceholders(text, placeholders) {
  for (const key in placeholders) {
    const regex = new RegExp(`{{\\s*${key}\\s*}}`, 'gi');
    text = text.replace(regex, placeholders[key]);
  }
  return text;
}

// Remplacement du placeholder "content" dans le template
const finalHtml = replacePlaceholders(template, { content: contenu });

// Retourne l'objet final
return {
  json: {
    final_content: finalHtml
  }
};

// Extraction et validation des données du node "Data Workflow"
const dataWorkflowNode = $('Data Workflow').first();
if (!dataWorkflowNode || !dataWorkflowNode.json) {
  throw new Error("Données manquantes dans le node 'Data Workflow'");
}
const dataWorkflow = dataWorkflowNode.json;

const enveloppe = dataWorkflow.html_enveloppe;
if (typeof enveloppe !== 'string') {
  throw new Error("L'enveloppe HTML (html_enveloppe) est manquante ou invalide dans 'Data Workflow'");
}

const header = dataWorkflow["Template Header"];
if (typeof header !== 'string') {
  throw new Error("Le header (Template Header) est manquant ou invalide dans 'Data Workflow'");
}

let footer = dataWorkflow["Template Footer"];
if (typeof footer !== 'string') {
  throw new Error("Le footer (Template Footer) est manquant ou invalide dans 'Data Workflow'");
}

const email = dataWorkflow["Contact Email"] || '';

// Extraction et validation des données d'entrée et d'autres nodes
const inputData = $input.first();
if (!inputData || !inputData.json) {
  throw new Error("Données d'entrée manquantes");
}
const content = inputData.json.final_content || '';

const replaceVariableNode = $('Replace Variable').first();
if (!replaceVariableNode || !replaceVariableNode.json) {
  throw new Error("Données manquantes dans le node 'Replace Variable'");
}
const link_id = replaceVariableNode.json.link_id;
if (!link_id) {
  throw new Error("link_id manquant dans le node 'Replace Variable'");
}

// Fonction utilitaire pour remplacer des placeholders de la forme {{ key }}
function replacePlaceholders(text, placeholders) {
  for (const key in placeholders) {
    const regex = new RegExp(`{{\\s*${key}\\s*}}`, 'gi');
    text = text.replace(regex, placeholders[key]);
  }
  return text;
}

// Remplacement des placeholders dans le footer
footer = replacePlaceholders(footer, {
  tracking_link: `<img src="https://suivi.boostperformance.ai/pixel.php?id=${link_id}" width="1" height="1" style="display:none;" />`,
  unsubscribe_link: `https://suivi.boostperformance.ai/communication/?email=${email}`
});

// Intégration des sections (header, content, footer) dans l'enveloppe HTML
const htmlFinal = enveloppe
  .replace('[[header]]', header)
  .replace('[[content]]', content)
  .replace('[[footer]]', footer);

// Retourner le résultat final
return [
  {
    json: {
      html: htmlFinal
    }
  }
];

// Récupération de la valeur des minutes à attendre
const minutesAttendre = $input.first().json["Minutes Attendre"];
//const minutesAvant = $input.first().json["Minutes Avant"];

// Récupération de la date de création au format ISO
const dateCreationStr = $('Data Workflow').first().json["Date Création"];

// Conversion de la chaîne en objet Date
const dateCreation = new Date(dateCreationStr);

// Calcul de la nouvelle date en ajoutant les minutes (60000 ms = 1 minute)
const nouvelleDate = new Date(dateCreation.getTime() + minutesAttendre * 60000);

// Retourner le résultat sous forme d'objet JSON (par exemple, sous la clé 'nouvelleDate')
return { nouvelleDate: nouvelleDate.toISOString() };

// Récupération de la valeur des minutes avant l'événement
const minutesAvant = $input.first().json["Minutes Avant"];

// Récupération de la date de création au format ISO
const dateEventStr = $('Get Event Details').first().json.Date;

// Conversion de la chaîne en objet Date
const dateEvent = new Date(dateEventStr);

// Calcul de la nouvelle date en soustrayant les minutes (60000 ms = 1 minute)
const nouvelleDate = new Date(dateEvent.getTime() - minutesAvant * 60000);

// Retourner le résultat sous forme d'objet JSON (par exemple, sous la clé 'nouvelleDate')
return { nouvelleDate: nouvelleDate.toISOString() };

// Extraction et validation des données des nœuds
const dataWorkflowItem = $('Data Workflow').item;
if (!dataWorkflowItem || !dataWorkflowItem.json) {
  throw new Error("Données manquantes dans le nœud 'Data Workflow'");
}
const dataWorkflow = dataWorkflowItem.json;

const updatedNode = $('Data Workflow Updated').first();
if (!updatedNode || !updatedNode.json) {
  throw new Error("Données manquantes dans le nœud 'Data Workflow Updated'");
}
const dataWorkflowUpdated = updatedNode.json;

// Récupération des données nécessaires
const prenom = dataWorkflow["Prénom"] || '';
let sujet = dataWorkflow.Sujet || '';
let contenu = dataWorkflow.Contenu || '';
let redirect_link = dataWorkflow["Lien redirection"] || '';

const sms_summary      = dataWorkflowUpdated['Résumé SMS'] || '';
const link_event       = dataWorkflowUpdated["Lien event"] || '';
const event_title      = dataWorkflowUpdated.Nom || '';
const event_description = dataWorkflowUpdated["Résumé"] || '';

// Fonction utilitaire pour remplacer des placeholders dans une chaîne
function replacePlaceholders(text, placeholders) {
  for (const key in placeholders) {
    const regex = new RegExp(`{{\\s*${key}\\s*}}`, 'gi');
    text = text.replace(regex, placeholders[key]);
  }
  return text;
}

// Génération d'un identifiant alphanumérique de la longueur spécifiée
function generateRandomId(length) {
  const chars = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
  let result = '';
  if (typeof crypto !== 'undefined' && crypto.getRandomValues) {
    const values = new Uint8Array(length);
    crypto.getRandomValues(values);
    for (let i = 0; i < length; i++) {
      result += chars.charAt(values[i] % chars.length);
    }
  } else {
    for (let i = 0; i < length; i++) {
      result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
  }
  return result;
}

// Générer un identifiant aléatoire de 10 caractères
const link_id = generateRandomId(10);

// Remplacement des placeholders dans le sujet
const sujetPlaceholders = {
  prenom: prenom,
  event_title: event_title,
  sms_summary: sms_summary,
  link_event: link_event
};
sujet = replacePlaceholders(sujet, sujetPlaceholders);

// Remplacement des placeholders dans le contenu (hors liens spéciaux)
const contenuPlaceholders = {
  prenom: prenom,
  event_title: event_title,
  event_description: event_description
};
contenu = replacePlaceholders(contenu, contenuPlaceholders);

// Remplacement du placeholder pour le lien de redirection (on encode l'URL pour éviter des problèmes)
const encodedRedirectLink = encodeURIComponent(redirect_link);
const redirectReplacement = `https://suivi.boostperformance.ai/clk/?id=${link_id}&link=${encodedRedirectLink}`;
contenu = contenu.replace(/{{\s*redirect_link\s*}}/gi, redirectReplacement);

// Remplacement du placeholder pour le tracking (injection de l'image)
const trackingReplacement = `<img src="https://suivi.boostperformance.ai/pixel.php?id=${link_id}" width="1" height="1" style="display:none;" />`;
contenu = contenu.replace(/{{\s*tracking_link\s*}}/gi, trackingReplacement);

// Encodage du sujet en Base64 UTF-8 (sans capture interne d'éventuelles erreurs)
const encodedSubject = `=?UTF-8?B?${Buffer.from(sujet, 'utf-8').toString('base64')}?=`;

// Retourner l'objet final incluant le sujet encodé, le sujet non encodé, le contenu et le link_id généré
return {
  json: {
    Sujet: encodedSubject,
    Sujet_not_encoded: sujet,
    Contenu: contenu,
    link_id: link_id
  }
};

const newFileName = 'image1.png';

const binaryData = {
  ...items[0].binary.data,
  fileName: newFileName,
  mimeType: 'image/png'
};

return [{
  json: items[0].json,
  binary: {
    data: binaryData
  }
}];
